ListItemRenderer.JavaScript:

putTaskAsDone: function()
{
	this.taskTitleLb.removeClass('strike');

	if( this.chk.getChecked() == true )
		this.taskTitleLb.addClass('strike');
},

deleteTask: function()
{
	this.sendMessage("deleteTask", {task: this.model});
},

showOptionsPopUp: function()
{
	var popUp = Phi.Mn.PopUpManager.createPopUp( MyFirstPopUp, {hideOnClick: false} );
	
	popUp.show( true );
	popUp.center();
},

onMDown: function()
{
}
